(9309, '235Z00000X', 'Speech, Language and Hearing Service Providers', 'Speech-Language Pathologist', NULL, 'The speech-language pathologist is the professional who engages in clinical services, prevention, advocacy, education, administration, and research in the areas of communication and swallowing across the life span from infancy through geriatrics. Speech-language pathologists address typical and atypical impairments and disorders related to communication and swallowing in the areas of speech sound production, resonance, voice, fluency, language (comprehension and expression), cognition, and feeding and swallowing.', '2002-04-01', '2024-11-15 15:13:16', '2024-11-15 15:13:16'),
(9310, '390200000X', 'Student, Health Care', 'Student in an Organized Health Care Education/Training Program', NULL, 'An individual who is enrolled in an organized health care education/training program leading to a degree, certification, registration, and/or licensure to provide health care. ', '2005-04-01', '2024-11-15 15:13:16', '2024-11-15 15:13:16'),
(9311, '242T00000X', 'Technologists, Technicians & Other Technical Service Providers', 'Perfusionist', NULL, 'A perfusionist operates extracorporeal circulation and autotransfusion equipment during any medical situation where it is necessary to support or temporarily replace the patient\'s circulatory or respiratory function. The perfusionist is knowledgeable concerning the variety of equipment available to perform extracorporeal circulation functions and is responsible, in consultation with the physician, for selecting the appropriate equipment and techniques to be used.', '2007-04-01', '2024-11-15 15:13:16', '2024-11-15 15:13:16'),
(9312, '247100000X', 'Technologists, Technicians & Other Technical Service Providers', 'Radiologic Technologist', NULL, 'An individual who is trained and qualified in the art and science of both ionizing and non-ionizing radiation for the purposes of diagnostic medical imaging, interventional procedures and therapeutic treatment.', '2002-04-01', '2024-11-15 15:13:16', '2024-11-15 15:13:16'),
